import React from "react";
import ReactDOM from "react-dom";

function Card(props) {

  const cards = {
  0: {
      name: "Beyonce",
      image: "https://blackhistorywall.files.wordpress.com/2010/02/picture-device-independent-bitmap-119.jpg",
      phone: "+123 456 789",
      email: "b@beyonce.com"
    },
  1: {
    name: "Jack Bauer",
    image: "https://pbs.twimg.com/profile_images/625247595825246208/X3XLea04_400x400.jpg",
    phone: "+987 654 321",
    email: "jack@nowhere.com"
    },
  2: {
    name: "Chuck Norris",
    image: "https://i.pinimg.com/originals/e3/94/47/e39447de921955826b1e498ccf9a39af.png",
    phone: "+918 372 574",
    email: "gmail@chucknorris.com"
    }
  };

  const output = {op: null}

  cards.forEach(props => {
    output.op = (
      output.op +
      <div>
        <h2>{props.name}</h2>
        <img
          src={props.image}
          alt="avatar_img"
        />
        <p>{props.phone}</p>
        <p>{props.email}</p>
      </div>
    );
  });
  return output.op
};

ReactDOM.render(
  <div>
    <h1>My Contacts</h1>
    <Card />
  </div>,
  document.getElementById("root")
);



